import React, { useEffect } from 'react'
import { BrowserRouter as Router,Route,Routes,NavLink} from 'react-router-dom';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import './App.css';
import { useState } from "react";
import validateUser from "./Service/LoginService";
import "bootstrap/dist/css/bootstrap.min.css";
import { logStatus } from './Service/LoginService';
import "./App.css";
import Routing from './routing';





 function App(){
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(logStatus);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit =async  (e) => {
    e.preventDefault();
    let user=await validateUser(userName, password);
    if (user) {
      setIsLoggedIn(true);
      setErrorMessage('');
    } else {
      setIsLoggedIn(false);
      setUserName('');
      setPassword('');
      setErrorMessage('Invalid username/Password');
    }
  };
 useEffect(()=>{
  setIsLoggedIn(logStatus);
 },[])

  return (
    <div>
        {!isLoggedIn ? (
        
        <>
        <nav class="navbar navbar-light bg-black">
        <h1 className='ml-2'>TeckGain</h1>
        </nav>
         <form  >
          <div className='form-group row border rounded mt-3'>
            
                   <label  htmlFor="userName" className="col-sm-6 col-form-label text-center">Username</label>
                   <div class="col-sm-6 my-auto">
                   <input
                     type="text"
                     className='form-control w-75 border-dark'
                     id="userName"
                     value={userName}
                     onChange={(e) => setUserName(e.target.value)}
                   />
                   </div>
          </div>       
          <div className='form-group row border rounded mt-2'>
                 <label  htmlFor="password" className="col-sm-6 col-form-label text-center">Password</label> 
                 <div class="col-sm-6 my-auto">
                   <input
                     type="password"
                     className='form-control w-75 border-dark'
                     id="password"
                     value={password}
                     onChange={(e) => setPassword(e.target.value)}
                   />
                   </div>
            </div> 
           <button type="button" id="submit"  onClick={handleSubmit}>
             Login
           </button>
           {errorMessage && <p className='errorMsg'>{errorMessage}</p>}
         </form>
         </>
       
        ) : (  
          
           <Routing />
           )}   
    </div>
  );
}	 	  	  
export default App;
